mapper.select:
select top ${pageSize} t0.*,t1.fid fmaid,t1.materialNumber ,t1.materialName ,t1.materialModel ,
t2.fid gID,t2.fnumber gNumber,'' gName,
t3.fid fstid,t3.stockNumber ,t3.stockName ,t4.fid cID,
t4.fnumber cNumber,t4.fname cName,t5.fid scID ,t5.fname scName,t5.fnumber scNumber
from T_VS_Inventory t0
left join T_VS_Material t1 on t0.fmaid=t1.fid
left join T_VS_GoodSeat t2 on t0.FGoodSeatID=t2.fid
left join T_VS_Stock t3 on t0.FStockID=t3.fid
left join t_vs_scabinet t4 on t2.fscabinetid=t4.fid
left join t_vs_cabinet t5 on t4.fcabinetid=t5.fid
where  t0.fqty>0 and t0.fid not in (
select top ${pageIndex} tt0.fid from T_VS_Inventory tt0
left join T_VS_Material tt1 on tt0.fmaid=tt1.fid
left join T_VS_GoodSeat tt2 on tt0.FGoodSeatID=tt2.fid
left join T_VS_Stock tt3 on tt0.FStockID=tt3.fid
left join t_vs_scabinet tt4 on tt2.fscabinetid=tt4.fid
left join t_vs_cabinet tt5 on tt4.fcabinetid=tt5.fid
where  tt0.fqty>0 and   convert(varchar,fdate,23) between #{sdate} and #{edate}

  and tt0.fmaid =#{fmaid}		

  and tt0.fgoodseatid=#{fgoodseatid}


  and tt0.fstockid =#{fstockid} 		

  and tt5.fid =#{fcabinetid} 		

  and tt4.fid =#{fscabinetid} 		

  and fbatch  like '%${fbatch}%'





order by tt2.fnumber
)

  and t0.fmaid =#{fmaid}		

  and t0.fgoodseatid=#{fgoodseatid}


  and t0.fstockid =#{fstockid} 		

  and t5.fid =#{fcabinetid} 		

  and t4.fid =#{fscabinetid} 		

  and fbatch  like '%${fbatch}%'



and convert(varchar,t0.fdate,23) between #{sdate} and #{edate}
order by t2.fnumber

select count(*) from T_VS_Inventory tt0
left join T_VS_Material tt1 on tt0.fmaid=tt1.fid
left join T_VS_GoodSeat tt2 on tt0.FGoodSeatID=tt2.fid
left join T_VS_Stock tt3 on tt0.FStockID=tt3.fid
left join t_vs_scabinet tt4 on tt2.fscabinetid=tt4.fid
left join t_vs_cabinet tt5 on tt4.fcabinetid=tt5.fid
where  tt0.fqty>0 and   convert(varchar,fdate,23) between #{sdate} and #{edate}

  and tt0.fmaid =#{fmaid}		

  and tt0.fgoodseatid=#{fgoodseatid}


  and tt0.fstockid =#{fstockid} 		

  and tt5.fid =#{fcabinetid} 		

  and tt4.fid =#{fscabinetid} 		

  and fbatch  like '%${fbatch}%'
